,"type":"address"},{"internalType":"bytes","name":"signatures","type":"bytes"},{"internalType":"address","name":"msgSender","type":"address"}],"name":"checkTransaction","outputs":[],"stateMutability":"view","type":"function"},{"inputs":[],"name":"controllerRegistry","outputs":[{"internalType":"contract IControllerRegistry","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address[]","name":"_members","type":"address[]"},{"internalType":"uint256","name":"threshold","type":"uint256"},{"internalType":"address","name":"_admin","type":"address"},{"internalType":"bytes32","name":"_label","type":"bytes32"},{"internalType":"string","name":"_ensString","type":"string"},{"internalType":"uint256","name":"expectedPodId","type":"uint256"},{"internalType":"string","name":"_imageUrl","type":"string"}],"name":"createPod","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_admin","type":"address"},{"internalType":"address","name":"_safe","type":"address"},{"internalType":"bytes32","name":"_label","type":"bytes32"},{"internalType":"string","name":"_ensString","type":"string"},{"internalType":"uint256","name":"expectedPodId","type":"uint256"},{"internalType":"string","name":"_imageUrl","type":"string"}],"name":"createPodWithSafe","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_context","type":"address"}],"name":"delegateSetup","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"safe","type":"address"},{"internalType":"address","name":"reverseRegistrar","type":"address"},{"internalType":"address","name":"previousModule","type":"address"},{"internalType":"address","name":"module","type":"address"}],"name":"disableModule","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"podId","type":"uint256"},{"internalType